contracts/NftAuction.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.4.0
pragma solidity ^0.8.20;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol"; //透明代理
// import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {console} from "hardhat/console.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "hardhat/console.sol";
contract NftAuction is Initializable, AccessControl {//, UUPSUpgradeable  {
    // 结构体
    struct Auction{
        // 卖家
        address seller;
        // 拍卖开始时间
        uint256 startTime;
        // 拍卖持续时间
        uint256 auctionDuration;
        // 起拍价
        uint256 startingBid;
        // 最高出价
        uint256 highestBid;
        // 最高出价者
        address highestBidder;
        // nft合约地址
        address nftContract;
        // nft的tokenId，通过nft合约地址和tokenId可以确定到那个具体的NFT
        uint256 tokenId;
        // 参与竞价的货币类型，0x表示eth，其他地址表示ERC20通证
        address tokenAddress;
        // 竞拍是否结束标志
        bool ended;
    }
    uint256 public auctionId;

    mapping (uint256 => Auction) public auctionIdAution;
    // 拍卖管理员
    address public admin;
    // bytes32 public constant ADMIN = keccak256("ADMIN");
    address public owner;

    mapping(address=>AggregatorV3Interface) public priceFeeds;
    mapping(address => bool) public priceFeedsExists;
    
    // constructor(){
    //     owner = msg.sender;
    // }

    // 如果使用Initializable进行合约升级，而且使用AccessControl进行权限管理的话，要在initialize中进行权限配置，不能再constructor中进行权限配置
    // initializer 修饰符确保 initialize 函数只能被调用一次
    // 问题：如何保证initialize的调用权限？不用保证，initialize方法只能调用一次，而通常是在部署的时候调用，也就是部署者调用，那么就能保证只有部署者能调用initialize
    function initialize(address _admin) public initializer  {
        owner = msg.sender;
        admin = _admin;
        // _grantRole(DEFAULT_ADMIN_ROLE, _admin);
    }

    /* sepolia
    eth->usd erc20TokenAddress: 0x0000000000000000000000000000000000000000, priceFeedAddr: 0x694AA1769357215DE4FAC081bf1f309aDC325306, 393890988244
    LINK->usd erc20TokenAddress: 0x779877A7B0D9E8603169DdbD7836e478b4624789, priceFeedAddr: 0xc59E3633BAAC79493d908e63626716e204A45EdF, 1842563497
    usdc->usd erc20TokenAddress: 0x1c7D4B196Cb0C7B01d743Fbc6116a902379C7238, priceFeedAddr: 0xA2F78ab2355fe2f984D808B5CeE7FD0A93D5270E, 99984973
    */
    // 问题，如果将erc20和priceFeedAddr的对应关系搞错了怎么办？
    function setPriceFeed(address erc20TokenAddress, address priceFeedAddr) public 
    // onlyRole(DEFAULT_ADMIN_ROLE) 
    onlyAdmin(msg.sender)
    {
        AggregatorV3Interface priceFeed = AggregatorV3Interface(priceFeedAddr);
        console.log(
            erc20TokenAddress,
            priceFeedAddr
        );
        priceFeeds[erc20TokenAddress] = priceFeed;
        priceFeedsExists[erc20TokenAddress] = true;
    }

    // Returns the latest answer.
    function getChainlinkDataFeedLatestAnswer(address erc20TokenAddress) public view returns (int) {
        AggregatorV3Interface priceFeed = priceFeeds[erc20TokenAddress];
        // data deconstruct
        (
            // uint80 roundId 
            ,
            int256 answer,
            //uint256 startedAt
            ,
            //uint256 updatedAt
            ,
            //uint80 answeredInRound
        ) = priceFeed.latestRoundData();
        return answer;
    }

    function convertToUsd(address erc20TokenAddress, uint256 amount) public view returns (uint256){
        // 获取喂价
        uint256 usdPrice = uint256(getChainlinkDataFeedLatestAnswer(erc20TokenAddress));
        
        // uint256 usdAmount = amount  * usdPrice;
        // return usdAmount;// /(10**8); 
        // 获取代币精度（USDC=6, LINK=18）
        uint256 decimals = 10**18;
        if(erc20TokenAddress!=address(0)){
            decimals = 10**IERC20Metadata(erc20TokenAddress).decimals();
        }
        
        // 正确计算：USD = (amount * usdPrice) / (10^8 * 10^decimals)
        uint256 usdAmount = (amount * usdPrice) / decimals;
        
        // 返回 USD 的 10^18 倍（用于后续计算）
        return usdAmount * 10**18;
    }

    //创建拍卖
    function createAuction(uint256 _auctionDuration, uint256 _startingBid, address seller, address _nftContract, uint256 _tokenId) public 
    // 如果只有管理员能创建拍卖，那卖家需要先把nft转给admin，需要先approve在transfer，然后admin再来创建拍卖，从admin的账户把nft转到该合约
    // 如果是人人都可以直接创建拍卖，那就是直接从卖家的账户把nft转到该合约
    onlyAdmin(seller)
    // onlyRole(DEFAULT_ADMIN_ROLE)
    // 起拍价大于0
    startingBidGreaterThanZero(_startingBid) 
    // 竞拍持续时间要大于某个时间，这里大于3分钟
    auctionDurationGreaterThanThreeMinites(_auctionDuration)
    {
        address nftOwner = IERC721(_nftContract).ownerOf(_tokenId);
        if(nftOwner!=address(this)){
            // 实例化要拍卖的nft，并将nft从管理员的钱包转入该拍卖合约中
            IERC721(_nftContract).safeTransferFrom(seller, address(this), _tokenId); //使用safeTransferFrom需要实现IERC721Receiver的onERC721Received接口
        }
        // IERC721(_nftContract).transferFrom(msg.sender, address(this), _tokenId);
        // nft转账成功后，保存该竞拍信息
        Auction memory auction = Auction({
            seller: seller,
            startTime: block.timestamp,
            auctionDuration: _auctionDuration,
            startingBid: _startingBid,
            highestBid: _startingBid, 
            highestBidder: seller,
            nftContract: _nftContract,
            tokenId: _tokenId,
            tokenAddress: address(0), //使用eth
            ended: false
        });
        auctionIdAution[auctionId] = auction;
        auctionId++;
    }

    // 竞拍
    function bidding(uint256 _auctionId, address erc20TokenAddress, uint256 erc20Amount) public payable 
    // 出价大于起拍价，大于最高价
    // 时间窗口内，竞拍未结束
    {
        console.log(
            "bidding1",
            erc20TokenAddress,
            priceFeedsExists[erc20TokenAddress]
        );
        require(priceFeedsExists[erc20TokenAddress], "Your ERC20 token is not supported to bid");
        
        Auction storage auction = auctionIdAution[_auctionId];
        
        require(!auction.ended && block.timestamp < auction.auctionDuration + auction.startTime, "Auction ends");
        // TODO 喂价，价格转换，eth转为usd，erc20转为usd
        uint256 amount;
        uint256 usdAmount;
        if(erc20TokenAddress == address(0)){
            // 转的是eth
            amount = msg.value;
            usdAmount = convertToUsd(address(0), amount);
        }else {
            //转的是erc20
            amount = erc20Amount;
            usdAmount = convertToUsd(erc20TokenAddress, amount);
        }
        console.log(
            "bidding2",
            erc20TokenAddress,
            usdAmount
        );
        // 将起拍价转为usd
        uint256 startingBidUsdAmont = convertToUsd(address(0), auction.startingBid);
        // 将上次的最高出价转为usd
        uint256 lastHighestBidUsdAmont = convertToUsd(auction.tokenAddress, auction.highestBid);

        // 对比本次出价的usd和起拍价、上次最高出价的usd
        require(usdAmount > startingBidUsdAmont && usdAmount > lastHighestBidUsdAmont, "Your bidding is too low");
        // 将出价者的erc20代币转移到本合约，执行此操作的主体应该是nftAuctionProxy
        if (erc20TokenAddress != address(0)) {
            IERC20(erc20TokenAddress).transferFrom(msg.sender, address(this), erc20Amount);
        }
        console.log(
            "bidding3",
            erc20TokenAddress,
            erc20Amount
        );
        // 将上一个最高出价者的代币退还
        if(auction.highestBid>auction.startingBid){
            if(auction.tokenAddress == address(0)){
                // 转的是eth, 退回eth
                payable(auction.highestBidder).transfer(auction.highestBid);
            }else {
                //转的是erc20，退回erc20
                // 问题：transfer是将调用者的代币转给参数中的to地址，这里的transfer的调用主体是出价者还是nftAuction还是nftAuctionProxy？
                // 用transfer的话是不是从出价者钱包转给上一个最高出价者？
                // 但是实际是从auction合约转给上个出价最好这
                IERC20(auction.tokenAddress).transfer(auction.highestBidder, auction.highestBid);
                // 使用transferFrom需要授权，将nftAuction合约的erc20代币授权给nftAuctionProxy
                // IERC20(auction.tokenAddress).transferFrom(address(this), auction.highestBidder, auction.highestBid);
            }
        }
        auction.highestBid = amount;
        auction.highestBidder = msg.sender;
        auction.tokenAddress = erc20TokenAddress;
        // auctionIdAution[_auctionId] = auction;
    }

    // 结束竞拍
    // 问题：有没有定时任务的实现方法
    // 问题：这个结束方法是不是谁来调用都可以
    function endAuction(uint256 _auctionId) public 
    // onlyRole(DEFAULT_ADMIN_ROLE) 
    onlyAdmin(msg.sender)
    {
        Auction storage auction = auctionIdAution[_auctionId];
        require(block.timestamp >= auction.auctionDuration + auction.startTime, "Time is not up yet, Auction ends failed");
        // 实例化要拍卖的nft，并将nft从本合约转入最高出价者的钱包
        IERC721(auction.nftContract).safeTransferFrom(address(this), auction.highestBidder, auction.tokenId);
        // 将eth或erc20从本合约转入seller的钱包中
        if(auction.highestBid > auction.startingBid){
            if(auction.tokenAddress == address(0)){
                //eth，从本合约将拍卖所得eth转给seller
                payable(auction.seller).transfer(auction.highestBid);
            } else {
                // erc20，将拍卖收到的erc20转给selleer
                IERC20(auction.tokenAddress).transfer(auction.seller, auction.highestBid);
                // IERC20(auction.tokenAddress).transferFrom(address(this), auction.seller, auction.highestBid);
            }
        }
        auction.ended = true;
    }

    function version() public pure virtual returns(string memory) {
        return "NftAuctionV1";
    }

    // function _authorizeUpgrade(address) internal view override {
    //     // 只有管理员可以升级合约
    //     require(msg.sender == admin, "Only admin can upgrade");
    // }
    
    // 实现 IERC721Receiver 接口
    function onERC721Received(
        address, 
        address, 
        uint256, 
        bytes calldata
    ) public pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    modifier onlyOwner(){
        require(msg.sender==owner, "Only owner allowed to create an auction");
        _;
    }

    modifier onlyAdmin(address _admin) {
        require(_admin==admin, "Only admin allowed to create an auction");
        _;
    }

    modifier startingBidGreaterThanZero(uint256 _startingBid){
        require(_startingBid > 0, "Starting bid must greater than 0");
        _;
    }

    modifier auctionDurationGreaterThanThreeMinites(uint256 _auctionDuration){
        require(_auctionDuration > 5, "Auction duration must greater than 5 seconds");
        _;
    }

}

contracts/NftAuctionFactory.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.4.0
pragma solidity ^0.8.20;
// 问题，怎么升级，每次拍卖合约升级后这里引入的NftAuction.sol还要变化吗，这个工厂合约要改吗，工厂合约也要用代理升级？
import {NftAuction} from "./NftAuction.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// 问题：使用工厂模式后，是每个拍卖都是创建一个新的拍卖合约吗，即一个合约对应一次拍卖？
contract NftAuctionFactory {
    address public owner;
    mapping(address => mapping(uint256 => NftAuction)) public nftContractAuctionContractMap;
    mapping(address => bool) public nftContractExists;

    constructor() {
        owner = msg.sender;
    }

    // 创建新拍卖（使用透明代理）
    function createAuction(
        uint256 _auctionDuration,
        uint256 _startingBid,
        address _nftContract,
        uint256 _tokenId
    ) external {
        require(IERC721(_nftContract).ownerOf(_tokenId) == msg.sender, "Not owner of NFT");
        require(_auctionDuration > 5, "Duration too short");

        // 1. 部署实现合约（NftAuction逻辑）
        NftAuction nftAuction = new NftAuction();
        nftAuction.initialize(msg.sender);

        // 设置喂价
        //eth
        // nftAuction.setPriceFeed(address(0), 0x694AA1769357215DE4FAC081bf1f309aDC325306);
        // //link
        // nftAuction.setPriceFeed(0x779877A7B0D9E8603169DdbD7836e478b4624789, 0xc59E3633BAAC79493d908e63626716e204A45EdF);
        // //usdc
        // nftAuction.setPriceFeed(0x1c7D4B196Cb0C7B01d743Fbc6116a902379C7238, 0xA2F78ab2355fe2f984D808B5CeE7FD0A93D5270E);
        
        // 5. 转移 NFT 到拍卖合约,
        // 问题：这个操作是否应该由工厂来做
        IERC721(_nftContract).transferFrom(
            msg.sender,
            address(nftAuction),
            _tokenId
        );
        
        //6. 创建拍卖
        // 问题：这个创建拍卖应该由谁来执行，是工厂合约还是拍卖合约？
        // 问题：函数调用者问题，这里要手动传入msg.sender，拍卖合约应不应该因为工厂合约而改变代码？
        nftAuction.createAuction(
            _auctionDuration,
            _startingBid,
            msg.sender,
            _nftContract,
            _tokenId
        );
        
        // 7. 记录合约
        bool exists = nftContractExists[_nftContract];
        if (exists) {
            nftContractAuctionContractMap[_nftContract][_tokenId] = nftAuction;
        } else {
            nftContractAuctionContractMap[_nftContract][_tokenId] = nftAuction;
            nftContractExists[_nftContract] = true;
        }
    }

    function getAuction(address nftContractAddress, uint256 tokenId) external view returns (NftAuction) {
        return nftContractAuctionContractMap[nftContractAddress][tokenId];
    }
}

